$(window).load(function() {
    $("#loading").delay(2000).fadeOut(500);
})

$(document).ready(function() {

    $('.fancybox').colorbox();

    $(".inline").colorbox({inline:true, width:"98%", height:"98%"});

    $(".inline1").colorbox({inline:true, width:"90%", height:"90%"});

    $(".iframe").colorbox({iframe:true, width:"80%", height:"80%"});



    // Animate Scroll
    var controller = new ScrollMagic({
        globalSceneOptions: {
            duration: $('section').height(),
            triggerHook: 0.025,
            reverse: true
        }
    });
    new ScrollScene({ triggerElement: '#intro' }).setClassToggle('#intro-anchor', 'active').addTo(controller);
    new ScrollScene({ triggerElement: '#section-1' }).setClassToggle('#anchor1', 'active').addTo(controller);
    new ScrollScene({ triggerElement: '#section-2' }).setClassToggle('#anchor2', 'active').addTo(controller);
    new ScrollScene({ triggerElement: '#section-3' }).setClassToggle('#anchor3', 'active').addTo(controller);
    controller.scrollTo(function (target) {
        TweenMax.to(window, 0.5, {
            scrollTo: {
                y: target,
                autoKill: true
            },
            ease: Cubic.easeInOut
        });
    });

    //ancora suave
    $(document).on('click', 'a[href^=#]', function (e) {
        var id = $(this).attr('href');
        if ($(id).length > 0) {
            e.preventDefault();
            controller.scrollTo(id);
            if (window.history && window.history.pushState) {
                history.pushState('', document.title, id);
            }
        }
    });




    //botao que oculta o menu principal
    $(".bt-menu").click(function(){
        $("#panel").slideToggle("slow");
        $(this).toggleClass("ativo", 300 ); 
        $(".icones-section-1").toggleClass("ocultar", 300 );

        return false;
    });

    //botao que oculta o menu principal
    $(".bt-menu3").click(function(){
        $("#panel3").slideToggle("slow");
        $(this).toggleClass("ativo", 300 ); 
        $(".map-image").toggleClass("ocultar", 300 );
  

        if($('.top-titulo').css('top')=='10px'){
            $('.top-titulo').animate({top: '500px'}, 400);        
        }else{
            $('.top-titulo').animate({top:10}, 1000); 
        }

        return false;
    });

    //botao que oculta o menu principal
    $(".bt-menu4").click(function(){
        $("#panel-clima").slideToggle("slow");
        $(this).toggleClass("ativo", 300 ); 
        
  

        if($('.top-titulo').css('top')=='10px'){
            $('.top-titulo').animate({top: '500px'}, 400);        
        }else{
            $('.top-titulo').animate({top:10}, 1000); 
        }

        return false;
    });
   
    // Botao que chama a apresentacao
    $('#toggle-content').click(function() {
        $('.hidden-content').toggleClass("visible-content", 500, "easeOutSine"); 
        $( ".hidden-content1" ).removeClass( "visible-content1" );
        $( ".hidden-content2" ).removeClass( "visible-content1" );
        

        $("#panel").slideToggle("slow");
        $(".bt-menu").toggleClass("ativo"); 
        $(".icones-section-1").toggleClass("ocultar", 300 );

        return false;
    });

    // Botao que chama a creditos
    $('#toggle-content1').click(function() {
        $('.hidden-content1').toggleClass("visible-content1", 500, "easeOutSine");
        $( ".hidden-content" ).removeClass( "visible-content" );
        $( ".hidden-content2" ).removeClass( "visible-content2" ); 
        

        $("#panel").slideToggle("slow");
        $(".bt-menu").toggleClass("ativo");
        $(".icones-section-1").toggleClass("ocultar", 300 ); 

        return false;
    });

    // Botao que chama referencias
    $('#toggle-content2').click(function() {
        $('.hidden-content2').toggleClass("visible-content2", 500, "easeOutSine");
        $( ".hidden-content" ).removeClass( "visible-content" );
        $( ".hidden-content1" ).removeClass( "visible-content1" ); 
        

        $("#panel").slideToggle("slow");
        $(".bt-menu").toggleClass("ativo"); 
        $(".icones-section-1").toggleClass("ocultar", 300 );

        return false;
    });

    //auxiliar que esconde o conteudo da apresentacao
    $('.hidden-content > .alert-close').on('click', function(c){
        $(this).parent().toggleClass("visible-content"); 
    }); 

    $('.hidden-content1 > .alert-close').on('click', function(c){
        $(this).parent().toggleClass("visible-content1"); 
    }); 

    $('.hidden-content2 > .alert-close').on('click', function(c){
        $(this).parent().toggleClass("visible-content2"); 
    }); 

    $('.single-item').slick({
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    });

    $('.tooltip').tooltipster({
        theme: '.tema-tooltipster',
        animation: 'grow',
    });

    $('.tooltip2').tooltipster({
        theme: '.tema2',
        animation: 'grow',
        arrow: false,
        position: 'right',
    });


    /*

    $('.video-aux').videoBG({
        mp4:'assets/video/final.mp4',
        ogv:'assets/video/final.ogv',
        webm:'assets/video/final.webm',
        zIndex:0,
        loop:false
    });

    $('.video-aux').show().delay(33700).fadeOut();
    
    */

        //paginacao
    $('.content-pagination > li').slice(1).hide();

     $('.pagination').jqPagination({
        max_page: Math.ceil($('.content-pagination > li').length / 1),
        paged: function (page) {
            $('.content-pagination > li').hide();
            $('.content-pagination > li').slice((page - 1) * 1, (page * 1)).show();
        }
    });



});


